import React, { Component } from 'react';
import { Route , withRouter} from 'react-router-dom';

class Button extends Component{
	
	constructor(props){
		super(props)
		this.handleClick = this.handleClick.bind(this);
	}

	handleClick(){
		this.props.toLogIn();
		this.props.history.push('/')
	}	

	render(){
		console.log("button et props", this.props)
    return(
    	<React.Fragment>
    	<h1>{this.props.isLogged ? "Connecté" : "Déconnecté" } </h1>
      <div class="card">
      <div class="inner">
    
          <div class="signup">
            <h3>Login</h3>
            <form>
              <div class="form-control">
                <input type="text" name="username" />
                <label for="username">Username</label>
              </div>
              <div class="form-control">
                <input type="password" name="password_" />
                <label for="password_">Password</label>
              </div>
              <button>a remplacer</button>
            </form>
          </div>
        </div>
        </div>
        <button onClick={this.handleClick}>  {this.props.isLogged ? "Se déconnecter" : "Se connecter" }  </button>
 		</React.Fragment>
      )
  }

    
}


export default withRouter(Button);
