import React, { Component } from 'react';
import {BrowserRouter as Router, Switch, Route, Link} from "react-router-dom";
import { withAutorization } from '../Autorization.js';

class Content extends Component{

	state = {
		qty: 0,
	}

  constructor(props){
    super(props)
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(){
  	if (this.props.isLogged == true){
  		  	this.props.addInCart(this.state.qty)
  	this.setState({
  		qty: this.state.qty + 1
  	})
  	}
  	
  	else {
  	 	alert("Il faut être connecté")
  	}

  	console.log(this.props.quantity)
  	
  }

	render(){
		return(
			<div>
				<div class="box box-2">
					<img src={require('./corona.jpg')}/>
					<h1>Covid-19</h1>
					<button onClick={this.handleClick}>Add Covid-19</button>
				</div>	
							<div class="box box-2">
					<img src={require('./corona.jpg')}/>
					<h1>Covid-18</h1>
					<button onClick={this.handleClick}>Add Covid-18</button>
				</div>	
								<div class="box box-2">
					<img src={require('./corona.jpg')}/>
					<h1>Covid-17</h1>
					<button onClick={this.handleClick}>Add Covid-17</button>
				</div>	
			</div>
		
			)
	}

    
}

export default withAutorization(Content);
