import React, { Component } from 'react';
import { Route , withRouter} from 'react-router-dom';

class Button extends Component{
	state = {
		username: "",
		password_: "",
	}

	constructor(props){
		super(props)
		this.handleClick = this.handleClick.bind(this);
	}

	handleClick(){
		this.props.toLogIn(this.state.username, this.state.password_);
		this.props.history.push('/connexion')
	}	

	handleChange = (event) => {
		console.log("event.target:", event.target)
		this.setState({[event.target.name]: event.target.value});
	}

	handleSubmit = (event) =>{
		console.log("username:", this.state.username);
		console.log("password:", this.state.password_);
		event.preventDefault();
	}

	



	render(){
    return(
    	<React.Fragment>
    	<h1>{this.props.isLogged ? "Connecté" : "Déconnecté" } </h1>
      <div class="card">
      <div class="inner">
    
          <div class="signup">
            <h3>Login</h3>
            <form onSubmit={this.handleSubmit}>
              <div class="form-control">
                <input type="text" name="username" onChange={this.handleChange}/>
                <label for="username">Username</label>
              </div>
              <div class="form-control">
                <input type="password" name="password_" onChange={this.handleChange}/>
                <label for="password_">Password</label>
              </div>
			  <button onClick={this.handleClick}>  {this.props.isLogged ? "Se déconnecter" : "Se connecter" }  </button>
            </form>
          </div>
        </div>
        </div>

 		</React.Fragment>
      )
  }

    
}


export default withRouter(Button);
