import Navigation from './components/navigation'
import Title from './components/title'
import Login from './components/login'
import Button from './components/content'
import React, { Component } from 'react';
import {BrowserRouter as Router, Switch, Route, Link} from "react-router-dom";
import "./App.css"

//Utiliser const AuthContext = React.createContext(null);
//Ensuite AuthContext.provider pour prendre le state et AuthContext.Consume pour le réutiliser de partout 

class App extends Component{
	render(){
		return(
		<Router>
			<div>
			<Navigation/>
			<hr/>
			<Switch>
			  <Route exact path="/">
					<Title/>       
			  </Route>
	          <Route path="/inscription">
	          		<Login/>
	          </Route>
	          <Route path="/connexion">
	            	<Button/>
	            	<Button2/>
	          </Route>
	          </Switch>
	       </div>
       	</Router>
			
		)
	}
}

const Button2 = WithAutorization(Button);

const AutorizationContext = React.createContext(null);

class AutorizationProvider{
	state = {
		isLogged: false,
		toLogIn: () => this.setState({isLogged: true}),
	};

	render(){
		return(
			<AutorizationContext.Provider value = {this.state}>
			{this.props.children}
			</AutorizationContext.Provider>
			);
	}
}

//

const WithAutorization = (Component) => {
	class NewComponent extends Component {
		render(){
			return (
				<AutorizationContext.Consumer>
				{value => <Component {...value} {...this.props} />}
				</AutorizationContext.Consumer>
				)
		}
	}
	return NewComponent
}






export default App;
