import Navigation from './components/navigation'
import Title from './components/title'
import Login from './components/login'
import Button from './components/content'
import React, { Component } from 'react';
import {BrowserRouter as Router, Switch, Route, Link} from "react-router-dom";
import "./App.css"




const AutorizationContext = React.createContext(null);

class AutorizationProvider extends Component{
	state = {
		isLogged: false,
		toLogIn: () => this.setState({isLogged: !this.state.isLogged}),
	};

	render(){
		return(
			<AutorizationContext.Provider value = {this.state}>
			{this.props.children}
			</AutorizationContext.Provider>
			);
	}
}

//

const withAutorization = (Component) => {
	class NewComponent extends Component {
		render(){
			return (
				<AutorizationContext.Consumer>
				{value => <Component {...value} {...this.props} />}
				</AutorizationContext.Consumer>
				)
		}
	
	}
	return NewComponent;
}

const Button2 = withAutorization(Button);



class App extends Component{
	render(){
		return(
			<AutorizationProvider>
		<Router>
			<div>
			<Navigation/>
			<hr/>
			<Switch>
			  <Route exact path="/">
					<Title/>       
			  </Route>
	          <Route path="/inscription">
	          		<Login/>
	          </Route>
	          <Route path="/connexion">
	            	<Button/>
	            	<Button2/>
	          </Route>
	          </Switch>
	       </div>
       	</Router>
       	</AutorizationProvider>
       		
			
		)
	}
}





export default App;
