import Navigation from './components/navigation'
import Title from './components/title'
import Login from './components/login'
import Button from './components/button'
import React, { Component } from 'react';
import {BrowserRouter as Router, Switch, Route, Link} from "react-router-dom";
import "./App.css"
import { withRouter } from 'react-router-dom'




const AutorizationContext = React.createContext(null);

class AutorizationProvider extends Component{
	state = {
		username: "",
		password_:"",
		isLogged: false,
		toLogIn: (username, passwd) => this.setState({
			isLogged: !this.state.isLogged,
			username: username,
			password_: passwd,
		}),
	};

	render(){
		console.log("Auth + pass:", this.state.username, " ", this.state.password_)
		return(
			<AutorizationContext.Provider value = {this.state}>
			{this.props.children}
			</AutorizationContext.Provider>
			);
	}
}

//

const withAutorization = (Component) => {
	class NewComponent extends React.Component {
		render(){
			return (
				<AutorizationContext.Consumer>
				{value => <Component {...value} {...this.props} />}
				</AutorizationContext.Consumer>
				)
		}
	
	}
	return NewComponent;
}

const Button2 = withAutorization(Button);



class App extends Component{
	render(){
		return(
			<AutorizationProvider>
		<Router>
			<div>
			<Navigation/>
			<hr/>
			<Switch>
			  <Route exact path="/">
					<Title/>       
			  </Route>
	          <Route path="/inscription">
	          		<Login/>
	          </Route>
	          <Route path="/connexion">
	            	<Button2/>
	          </Route>
	          </Switch>
	       </div>
       	</Router>
  	     	</AutorizationProvider>
       		
			
		)
	}
}





export default App;
