import React, { Component } from 'react';
import {BrowserRouter as Router, Switch, Route, Link} from "react-router-dom";
import { withAutorization } from '../Autorization.js';

class Content extends Component{

	state = {
		covid19: 0,
		covid18: 0,
		covid17: 0,
	}

  constructor(props){
    super(props)
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = (event) => {
  	if (this.props.isLogged == true){
  		if (event.target.id == "covid-19"){
  			console.log("je passe")
  		this.setState({
  			covid19: this.state.covid19 + 1,
  		})


  		}
  		  	this.props.addInCart()
  		  	console.log(this.props.quantity)

  	}
  	else {
  	 	alert("Il faut être connecté")
  	}


	console.log("event.target", event.target.name)
  	console.log(this.props.quantity)
  	
  }

	render(){
		return(
			<div>
				<div class="box box-2">
					<img src={require('./corona.jpg')}/>
					<h1>Covid-19</h1>
					<button onClick={this.handleClick} id="covid-19">Add Covid-19</button>
				</div>	
							<div class="box box-2">
					<img src={require('./corona.jpg')}/>
					<h1>Covid-18</h1>
					<button onClick={this.handleClick} id="covid-18">Add Covid-18</button>
				</div>	
								<div class="box box-2">
					<img src={require('./corona.jpg')}/>
					<h1>Covid-17</h1>
					<button onClick={this.handleClick} if="covid-17">Add Covid-17</button>
				</div>	
			</div>
		
			)
	}

    
}

export default withAutorization(Content);
